'use strict';

module.exports = {
  toPixel: function toPixel(pixel) {
    if (typeof pixel === 'number') {
      return pixel + 'px';
    } else {
      return pixel;
    }
  },
  toDip: function toDip(pixel) {
    var px = pixel;
    if (!(typeof pixel === 'number')) {
      px = parseFloat(pixel);
    }
    return px / 2;
  },
  toRem: function toRem(pixel) {
    var px = pixel;
    if (!(typeof pixel === 'number')) {
      px = parseFloat(pixel);
    }
    return px / 100 + 'rem';
  }
};